"use strict";
const currentDate = new Date().getTime();
const inputDate = new Date('2024-12-04T21:49:14.827Z').getTime();
let resultText;
const result = inputDate - currentDate;
if (result > 1 * 60 * 60 * 1000) {
    resultText = 'Còn thời gian hủy';
}
else if (result < 1 * 60 * 60 * 1000) {
    resultText = 'Hết thời gian hủy';
}
else {
    resultText = 'Ngày nhập vào bằng ngày hiện tại!';
}
console.log(new Date());
console.log(result);
console.log(resultText);
